INSERT INTO `s_book` VALUES ('016048c62ca58c9920b6977ace7dbf10','9e73ec0f35887b611f53dd436a46b293','/Public/Upload//book/img/2018-02-03/5a75428ad2731.jpg','&lt;p&gt;&lt;br&gt;&lt;/p&gt;','学点急救知识——如何包扎止血',3,'&lt;p&gt;包扎是外伤现场应急处理的重要措施之一。及时正确的包扎，可以达到压迫止血、减少感染、保护伤口、减少疼痛，以及固定敷料和夹板等目的。今日，就让我们一起跟着视频来学习一些急救知识吧！&lt;br&gt;&lt;/p&gt;','&lt;p&gt;闵行区虹桥镇社区学校&lt;/p&gt;','[{\"type\":\"video\",\"title\":\"\\u70b9\\u51fb\\u89c2\\u770b\\u5fae\\u8bfe\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/video\\/2018-02-03\\/5a7542caac33e.mp4\",\"is_head\":\"0\"}]',0,1517633961,1517634258),('0237b5b52b901d86790ede1d04097abf','9d4517d18f3d102363c6cb7fefc310c8','/Public/Upload//book/img/2018-02-09/5a7d1674ddb80.jpg','&lt;p&gt;朱翔非&lt;/p&gt;','中华孝道',2,'&lt;p&gt;这是一部凝聚中国人亲情的经典，它塑造了中华民族的精神气质。在历史上占有极为重要的地位，这部经就是儒家的《孝经》，《孝经》是儒家经典中最薄的一本书，但它却承载着最厚重的一个字“孝”。&lt;br&gt;&lt;/p&gt;','&lt;p&gt;百家讲坛&lt;/p&gt;','[{\"type\":\"audio\",\"title\":\"01 \\u5b5d\\u7ecf\\u4f20\\u4e16\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/audio\\/2018-02-09\\/5a7d16a56b5a9.mp3\",\"is_head\":\"0\"},{\"type\":\"audio\",\"title\":\"02 \\u5b5d\\u7684\\u529b\\u91cf\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/audio\\/2018-02-09\\/5a7d16bab7ae5.mp3\",\"is_head\":\"0\"},{\"type\":\"audio\",\"title\":\"03 \\u5bb6\\u6709\\u5bb6\\u98ce\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/audio\\/2018-02-09\\/5a7d16d8b0193.mp3\",\"is_head\":\"0\"},{\"type\":\"audio\",\"title\":\"04 \\u5b5d\\u7684\\u771f\\u76f8\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/audio\\/2018-02-09\\/5a7d16f49d119.mp3\",\"is_head\":\"0\"},{\"type\":\"audio\",\"title\":\"05 \\u975e\\u5e38\\u5e95\\u9650\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/audio\\/2018-02-09\\/5a7d1709418c5.mp3\",\"is_head\":\"0\"},{\"type\":\"audio\",\"title\":\"06 \\u4eba\\u9645\\u7684\\u6e29\\u60c5\",\"file_src\":\"\\/Public\\/Upload\\/\\/book\\/audio\\/2018-02-09\\/5a7d17358a18b.mp3\",\"is_head\":\"0\"},{\"type\":\"audio\",\"title\":\"07 \\u957f\\u5927\\u6210\\u4eba\",\"file_src\":\